import React from "react";

import Titles from "./components/Titles";
import Form from "./components/Form"; 
import Weather from "./components/Weather";

const API_KEY = "6b4fe8e189e685576ffb4332205407d0";

class App extends React.Component {
  state = {
    temperature: undefined,
    city: undefined,
    country: undefined,
    humidity: undefined,
    description: undefined,
    error: undefined
  }
  getWeather = async (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    //const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=metric`);

    const api_call = await fetch(`https://cricapi.com/api/playerFinder?apikey=${city}&name=${country}`)



    //const api_call = await fetch(`https://cricapi.com/api/playerStats?apikey=${city}&pid=${country}`)

    //api.openweathermap.org/data/2.5/weather?q={city name}&appid={your api key}
    const data = await api_call.json();

    this.setState({
        temperature: data.data[0].pid,
        city: undefined,
        country: undefined,
        humidity: undefined,
        description: undefined,
        error: undefined
      });
    /*if (city && country) {
      this.setState({
        temperature: data.main.temp,
        city: data.name,
        country: data.sys.country,
        humidity: data.main.humidity,
        description: data.weather[0].description,
        error: ""
      });
    } else {
      this.setState({
        temperature: undefined,
        city: undefined,
        country: undefined,
        humidity: undefined,
        description: undefined,
        error: "Please enter the values."
      });
    }*/
  }
  render() {
    return (
      <div>
        <div className="wrapper">
          <div className="main">
            <div className="container">
              <div className="row">
                <div className="col-xs-5 title-container">
                  <Titles />
                </div>
                <div className="col-xs-7 form-container">
                  <Form getWeather={this.getWeather} />
                  <Weather 
                    temperature={this.state.temperature} 
                    humidity={this.state.humidity}
                    city={this.state.city}
                    country={this.state.country}
                    description={this.state.description}
                    error={this.state.error}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default App;
